mapper.select:
SELECT ci.*, ic.description AS category_description
FROM client_invoice ci
LEFT JOIN invoice_category ic ON ic.id = ci.category_id
WHERE ci.point_of_sale = #{pointOfSale}
  AND ci.invoice_number = #{invoiceNumber}

  AND ci.invoice_type = #{invoiceType}

ORDER BY ci.id DESC
LIMIT 1

SELECT ci.*
FROM client_invoice ci
WHERE 1 = 1


        ci.point_of_sale = #{pointOfSale}


        OR ci.point_of_sale LIKE CONCAT('%', #{pointOfSaleSuffix})


  AND ci.invoice_number LIKE CONCAT('%', #{numberSuffix})

  AND ci.invoice_type = #{invoiceType}


  AND ci.client_id = #{clientId}

ORDER BY ci.id DESC
LIMIT 1
